public class HammingDistance {
    public static int calculateHD(String s, String t) {
        int count = 0;
        for(int i = 0; i < s.length(); i++) {
            if(s.charAt(i) != t.charAt(i))
                count++;
        }
        return count;
    }

    public static void main(String[] args) {
        System.out.println(calculateHD("",
                ""));
    }
}
