import java.io.BufferedReader;
import java.io.FileReader;
import java.io.IOException;
import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

public class Translating_RNA_into_protein {

    // create ArrayList with single codons from rna sequence string
    public static ArrayList<String> create_codons(String RNA_seq) {

        ArrayList<String> codons = new ArrayList<>();
        int i = 0;
        while(i + 2 < RNA_seq.length()) {
            codons.add(RNA_seq.substring(i,i+3));
            i+= 3;
        }
        return codons;
    }

    // read in codon table as a string and then assign codons as keys and proteins as values in a hashmap
    public static HashMap<String, String> getCodonProteinMap() throws IOException {
        String codon_table;
        ArrayList<String> codons_n_proteins = new ArrayList<>();
        HashMap<String, String> codon_protein = new HashMap<>();

        try {
            BufferedReader br = new BufferedReader(new FileReader("/Users/mad_hatter/IdeaProjects/Rosalind/src/txt_files/codon_table.txt"));
            String line = br.readLine();
            StringBuilder stringBuilder = new StringBuilder();

            while(line != null) {
                stringBuilder.append(line + "\n");
                line = br.readLine();

            }
            codon_table = stringBuilder.toString();

            // PROBLEM: How can I extract codons and associated proteins from codon_table and assign codons as keys and proteins as values in
            // codon_proteins HashMap??
            // SOLUTION: iterate through whole string, separate at empty spaces. enter all substrings into arraylist and then assign entries to
            // hashmap, alternating keys and values

            int i = 0;
            int lastIndex = 0;

            while (i < codon_table.length()) {
                if (Character.isWhitespace(codon_table.charAt(i))) {
                    String substring = codon_table.substring(lastIndex, i);
                    if (!substring.isEmpty())
                        codons_n_proteins.add(substring.trim());
                    lastIndex = i + 1;
                }
                i++;
            }

            for(int j = 0; j < codons_n_proteins.size() - 1; j += 2) {
                    codon_protein.put(codons_n_proteins.get(j), codons_n_proteins.get(j + 1));
            }

        }
        catch (IOException e) {
            System.out.println("File cound not be found");
        }

        return codon_protein;
    }

    public static String translate(String rna_seq) throws IOException {
        List<String> codons = create_codons(rna_seq);
        List<String> proteins = new ArrayList<>();
        StringBuilder sb = new StringBuilder();
        int i = 0;

        for(String codon: codons) {
            proteins.add(i, getCodonProteinMap().get(codon));
            i++;
        }
        for(String protein: proteins) {
            sb.append(protein);
        }
        String proteins_str = sb.toString();
        String final_protein = null;

        for(int g = 0; g < proteins_str.length(); g++) {
            if(proteins_str.charAt(g) == 'S' && proteins_str.charAt(g+1) == 't' && proteins_str.charAt(g+2) == 'o' && proteins_str.charAt(g+3) == 'p') {
                final_protein = proteins_str.substring(0, g);
                break;}
        }
    return final_protein;
    }

    public static void main(String[] args) throws IOException {
        System.out.println(getCodonProteinMap().size());
        System.out.println(translate("\\n"));

    }
}

